scripts.release:
release() {
 echo "[release] $1"; [ "$1" != 'major' ] && [ "$1" != 'minor' ] && [ "$1" != 'patch' ] && echo "We need 1 parameter with value major|minor|patch instead of '$1'.
Example: npm run release -- minor" && exit 1;
 npm version $1 --message "Version %s

Changes:

" 
 }

release
